function calculateWPM() {
    var timeArr = [];
    var time = document.getElementById("time").innerHTML;
    timeArr.push(time.slice(0, 2));
    timeArr.push(time.slice(3, 5) / 60);
    timeArr.push(time.slice(6, 8) / 6000);
    var timeInMinutes = parseFloat(timeArr[0]) + parseFloat(timeArr[1]) + parseFloat(timeArr[2]);
    var wpm = (correctLetters / 5) / timeInMinutes;
    var rawWpm = (text.length / 5) / timeInMinutes;
    return Math.round(wpm);
}

function calculateRawWPM() {
    var timeArr = [];
    var time = document.getElementById("time").innerHTML;
    timeArr.push(time.slice(0, 2));
    timeArr.push(time.slice(3, 5) / 60);
    timeArr.push(time.slice(6, 8) / 6000);
    var timeInMinutes = parseFloat(timeArr[0]) + parseFloat(timeArr[1]) + parseFloat(timeArr[2]);
    var rawWpm = (text.length / 5) / timeInMinutes;
    return Math.round(rawWpm);
}

function timeToString(time) {
    let diffInHrs = time / 3600000;
    let hh = Math.floor(diffInHrs);
  
    let diffInMin = (diffInHrs - hh) * 60;
    let mm = Math.floor(diffInMin);
  
    let diffInSec = (diffInMin - mm) * 60;
    let ss = Math.floor(diffInSec);
  
    let diffInMs = (diffInSec - ss) * 100;
    let ms = Math.floor(diffInMs);
  
    let formattedMM = mm.toString().padStart(2, "0");
    let formattedSS = ss.toString().padStart(2, "0");
    let formattedMS = ms.toString().padStart(2, "0");
  
    return `${formattedMM}:${formattedSS}.${formattedMS}`;
}

function printTxt(txt) {
    document.getElementById("time").innerHTML = txt;
}

let startTime;
let elapsedTime = 0;
let timerInterval;

function start() {
    startTime = Date.now() - elapsedTime;
    timerInterval = setInterval(function printTime() {
        elapsedTime = Date.now() - startTime;
        printTxt(timeToString(elapsedTime));
    }, 10);
}
  
function pause() {
    clearInterval(timerInterval);
}
  
function reset() {
    clearInterval(timerInterval);
    print("00:00:00");
    elapsedTime = 0;
}